showAllItems();const contactDiv=document.getElementById("contactContainer");function showAllItems(){fetch("https://randomuser.me/api/?results=20&nat=NL").then(function(t){return t.json()}).then(function(t){for(let n=0;n<t.results.length;n++){const e=t.results[n],o=`<a class='contact' onclick="getPersonFromLocalStorage('person${n}')" href=#><img src='${e.picture.thumbnail}' /><p>${e.name.first}  ${e.name.last}</p></a>`;contactDiv.innerHTML+=o,localStorage.setItem(`person${n}`,JSON.stringify(e))}console.table(t.results[0])})}function getPersonFromLocalStorage(t){let n=JSON.parse(localStorage.getItem(t));console.table(n.name);let e=document.getElementById("userInfo");e.style="display: block;",e.innerHTML="",e.innerHTML+=`\n        <img src="${n.picture.large}" alt="Picture of person"/>\n        <button id="toggleUserInfo" class="UserInfoToggle" onclick="toggleUserInfo()">X</button>\n        <div class="info">\n            <h1>${n.name.first} ${n.name.last}</h1>\n            <table>\n                <tr>\n                    <th>Phone number</th>\n                </tr>\n                <tr>\n                    <td>${n.cell}</td>\n                </tr>\n                <tr>\n                    <th>E-mail address</th>\n                </tr>\n                <tr>\n                    <td>${n.email}</td>\n                </tr>\n                <tr>\n                    <th>Address</th>\n                </tr>\n                <tr>\n                    <td>${n.location.street}</td>\n                </tr>\n            </table>\n        </div>\n    `}const toggleUserInfo=()=>{userInfo.style="display:none;"};